/* ==UserStyle==
@name Youtube fix
@namespace Ayato-san
@version 1.0.0
@description Fix youtube issue
@author Ayato-san
@homepageURL https://github.com/Ayato-san/stylus/tree/main
@updateURL https://raw.githubusercontent.com/Ayato-san/stylus/refs/heads/main/youtube.user.css
@license MIT
@preprocessor stylus
==/UserStyle== */
@-moz-document domain("youtube.com") {
  :root {
      --yt-frosted-glass-mobile: rgba(255, 255, 255, 0.7);
      --yt-frosted-glass-desktop: rgba(255, 255, 255, 0.7);
  }
  #frosted-glass.with-chipbar.ytd-app {
      height: 56px;
  }

  html[dark],
  [dark] {
      --yt-frosted-glass-mobile: rgba(36, 39, 58, 0.7);
      --yt-frosted-glass-desktop: rgba(36, 39, 58, 0.7);
  }
}

/* ==UserStyle==
@name Unneeded content blocking
@namespace Ayato-san
@version 1.0.4
@description blocks unneeded content
@author Ayato-san
@homepageURL https://github.com/Ayato-san/stylus/tree/main
@updateURL https://raw.githubusercontent.com/Ayato-san/stylus/refs/heads/main/unneeded_content.user.css
@license MIT
@preprocessor stylus
==/UserStyle== */
@-moz-document domain("youtube.com") {
  ytd-rich-section-renderer.ytd-rich-grid-renderer.style-scope,
  ytd-reel-shelf-renderer.ytd-item-section-renderer.style-scope,
  ytd-guide-section-renderer.ytd-guide-renderer.style-scope:nth-of-type(3),
  ytd-guide-section-renderer.ytd-guide-renderer.style-scope:nth-of-type(4),
  #scroll-container > .yt-chip-cloud-renderer.style-scope,
  #header > .ytd-rich-grid-renderer.style-scope,
  ytd-guide-section-renderer:first-child > div > ytd-guide-entry-renderer:nth-child(2),
  ytd-reel-shelf-renderer.ytd-watch-next-secondary-results-renderer.style-scope {
    display: none !important;
  }
}

@-moz-document domain("twitch.tv") {
  .Layout-sc-1xcs6mc-0.hTjsXl.front-page-carousel[data-a-target="front-page-carousel"],
  .hKEeil.Layout-sc-1xcs6mc-0,
  .navigation-link,
  .jNQxNh.Layout-sc-1xcs6mc-0,
  .top-nav__external-link,
  .ScCoreButton-sc-ocjdkq-0.iPkwTD.ScButtonIcon-sc-9yap0r-0.dcNXJO[aria-label="Plus d'options"],
  .side-nav-section:is([aria-label="Chaînes live"],[aria-label="Catégories recommandées"]),
  #one-tap-store-id,
  .front-page-carousel
  .side-nav-show-more-toggle__button,
  .Layout-sc-1xcs6mc-0.gEvECC{
    display: none !important;
  }
}

@-moz-document domain("tl.plaync.com") {
  div.nc-banner.is-show[data-type=toast] {
    display: none !important;
  }
}

/* ==UserStyle==
@name AniList/AniChart Catppuccin
@namespace github.com/catppuccin/userstyles/styles/anilist
@homepageURL https://github.com/catppuccin/userstyles/tree/main/styles/anilist
@version 2025.09.06
@updateURL https://github.com/catppuccin/userstyles/raw/main/styles/anilist/catppuccin.user.less
@supportURL https://github.com/catppuccin/userstyles/issues?q=is%3Aopen+is%3Aissue+label%3Aanilist
@description Soothing pastel theme for AniList and AniChart
@author Catppuccin
@license MIT

@preprocessor less
@var select lightFlavor "Light Flavor" ["latte:Latte*", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkFlavor "Dark Flavor" ["latte:Latte", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve*", "red:Red", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]

@var select contrastColor "Contrast Color" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve", "red:Red*", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]
==/UserStyle== */

@import "https://userstyles.catppuccin.com/lib/lib.less";

@-moz-document domain("anilist.co"), domain("anichart.net") {
  body:not(.site-theme-dark) {
    #catppuccin(@lightFlavor);
  }
  .site-theme-dark {
    #catppuccin(@darkFlavor);
  }

  #catppuccin(@flavor) {
    #lib.palette();

    @contrast: @catppuccin[@@flavor][@@contrastColor];

    #lib.defaults();

    & when (@flavor = latte) {
      --color-blue-dim: #lib.rgbify(desaturate(lighten(@accent, 10%), 10%))[];

      .nav[data-v-62eacfff],
      .solid[data-v-62eacfff],
      .transparent[data-v-62eacfff]:hover {
        background: darken(@text, 10%);
        color: @base;
      }

      .nav-unscoped.transparent {
        background: fade(darken(@text, 10%), 50%);
      }
    }

    --color-background: #lib.rgbify(@crust)[];
    --color-foreground: #lib.rgbify(@base)[];
    --color-foreground-grey: #lib.rgbify(@surface0)[];
    --color-foreground-grey-dark: #lib.rgbify(darken(@surface0, 5%))[];
    --color-foreground-blue: #lib.rgbify(@mantle)[];
    --color-foreground-blue-dark: #lib.rgbify(@mantle)[];
    --color-background-blue-dark: #lib.rgbify(@subtext1)[];
    --color-overlay: #lib.rgbify(@surface0)[];
    --color-shadow: #lib.rgbify(@mantle)[];
    --color-shadow-dark: #lib.rgbify(darken(@text, 10%))[];
    --color-shadow-blue: #lib.rgbify(@blue)[];
    --color-text: #lib.rgbify(@text)[];
    --color-text-light: #lib.rgbify(@subtext1)[];
    --color-text-lighter: #lib.rgbify(@subtext0)[];
    --color-text-bright: #lib.rgbify(if(@flavor = latte, @crust, @text))[];
    --color-blue: #lib.rgbify(@accent)[];
    --color-white: 255, 255, 255;
    --color-black: 0, 0, 0;
    --color-red: #lib.rgbify(@contrast)[];
    --color-peach: #lib.rgbify(@maroon)[];
    --color-orange: #lib.rgbify(@peach)[];
    --color-yellow: #lib.rgbify(@yellow)[];
    --color-green: #lib.rgbify(@green)[];
    --color-background-1200: #lib.rgbify(@text)[];
    --color-background-1100: #lib.rgbify(@text)[];
    --color-background-1000: #lib.rgbify(@text)[];
    --color-background-900: #lib.rgbify(@subtext1)[];
    --color-background-800: #lib.rgbify(@subtext0)[];
    --color-background-700: #lib.rgbify(@surface1)[];
    --color-background-600: #lib.rgbify(@surface0)[];
    --color-background-500: #lib.rgbify(@surface0)[];
    --color-background-400: #lib.rgbify(@surface0)[];
    --color-background-300: #lib.rgbify(@base)[];
    --color-background-200: #lib.rgbify(@mantle)[];
    --color-background-100: #lib.rgbify(@crust)[];
    --color-gray-1200: #lib.rgbify(@text)[];
    --color-gray-1100: #lib.rgbify(@text)[];
    --color-gray-1000: #lib.rgbify(@text)[];
    --color-gray-900: #lib.rgbify(@text)[];
    --color-gray-800: #lib.rgbify(@overlay2)[];
    --color-gray-700: #lib.rgbify(@overlay1)[];
    --color-gray-600: #lib.rgbify(@overlay0)[];
    --color-gray-500: #lib.rgbify(@surface2)[];
    --color-gray-400: #lib.rgbify(@surface1)[];
    --color-gray-300: #lib.rgbify(@surface0)[];
    --color-gray-200: #lib.rgbify(@mantle)[];
    --color-gray-100: #lib.rgbify(@crust)[];
    --color-blue-100: #lib.rgbify(@text)[];
    --color-blue-200: #lib.rgbify(@text)[];
    --color-blue-300: #lib.rgbify(@text)[];
    --color-blue-400: #lib.rgbify(@sapphire)[];
    --color-blue-500: #lib.rgbify(@sapphire)[];
    --color-blue-600: #lib.rgbify(@blue)[];
    --color-blue-700: #lib.rgbify(@blue)[];
    --color-blue-800: #lib.rgbify(@blue)[];
    --color-blue-900: #lib.rgbify(@blue)[];
    --color-blue-1000: #lib.rgbify(@blue)[];
    --color-green-100: #lib.rgbify(@teal)[];
    --color-green-200: #lib.rgbify(@green)[];
    --color-green-300: #lib.rgbify(@green)[];
    --color-green-400: #lib.rgbify(@green)[];
    --color-green-500: #lib.rgbify(@green)[];
    --color-green-600: #lib.rgbify(@green)[];
    --color-green-700: #lib.rgbify(@green)[];
    --color-green-800: #lib.rgbify(@green)[];
    --color-red-100: #lib.rgbify(@text)[];
    --color-red-200: #lib.rgbify(@flamingo)[];
    --color-red-300: #lib.rgbify(@maroon)[];
    --color-red-400: #lib.rgbify(@red)[];
    --color-red-500: #lib.rgbify(@red)[];
    --color-red-600: #lib.rgbify(@red)[];
    --color-red-700: #lib.rgbify(@red)[];

    & when not(@flavor = latte) {
      --color-background: #lib.rgbify(@crust)[];
      --color-foreground: #lib.rgbify(@base)[];
      --color-foreground-grey: #lib.rgbify(@surface0)[];
      --color-foreground-grey-dark: #lib.rgbify(darken(@surface0, 5%))[];
      --color-foreground-blue: #lib.rgbify(@mantle)[];
      --color-foreground-blue-dark: #lib.rgbify(@mantle)[];
      --color-text: #lib.rgbify(@text)[];
      --color-text-light: #lib.rgbify(@subtext0)[];
      --color-text-lighter: #lib.rgbify(@subtext1)[];
      --color-shadow-dark: #lib.rgbify(@crust)[];
      --color-shadow-blue: #lib.rgbify(@crust)[];
      --color-background-1200: #lib.rgbify(@text)[];
      --color-background-1100: #lib.rgbify(@text)[];
      --color-background-1000: #lib.rgbify(@text)[];
      --color-background-900: #lib.rgbify(@subtext1)[];
      --color-background-800: #lib.rgbify(@subtext0)[];
      --color-background-700: #lib.rgbify(@surface1)[];
      --color-background-600: #lib.rgbify(@surface0)[];
      --color-background-500: #lib.rgbify(@surface0)[];
      --color-background-400: #lib.rgbify(@surface0)[];
      --color-background-300: #lib.rgbify(@base)[];
      --color-background-200: #lib.rgbify(@mantle)[];
      --color-background-100: #lib.rgbify(@crust)[];
      --color-gray-1200: #lib.rgbify(@text)[];
      --color-gray-1100: #lib.rgbify(@text)[];
      --color-gray-1000: #lib.rgbify(@text)[];
      --color-gray-900: #lib.rgbify(@text)[];
      --color-gray-800: #lib.rgbify(@overlay2)[];
      --color-gray-700: #lib.rgbify(@overlay1)[];
      --color-gray-600: #lib.rgbify(@overlay0)[];
      --color-gray-500: #lib.rgbify(@surface2)[];
      --color-gray-400: #lib.rgbify(@surface1)[];
      --color-gray-300: #lib.rgbify(@surface0)[];
      --color-gray-200: #lib.rgbify(@mantle)[];
      --color-gray-100: #lib.rgbify(@crust)[];

      --color-blue-dim: #lib.rgbify(desaturate(darken(@accent, 10%), 10%))[];
    }

    /* DARK THEME NAV BAR */
    .nav-unscoped when not(@flavor = latte) {
      background-color: @mantle;
    }
    .nav-unscoped.transparent when not(@flavor = latte) {
      background-color: fade(@mantle, 50%);
    }
    .nav-unscoped.transparent:hover when not(@flavor = latte) {
      background-color: @mantle;
      color: @subtext0;
    }

    /* -----------------PROFILE FIXES----------------- */
    #profileColor(@color) {
      --color-blue: #lib.rgbify(@color)[];
      & when (@flavor = latte) {
        --color-blue-dim: #lib.rgbify(lighten(@color, 10%))[];
      }
      & when not(@flavor = latte) {
        --color-blue-dim: #lib.rgbify(darken(@color, 10%))[];
      }
      .progress .bar {
        background: linear-gradient(270deg, darken(@color, 10%), @color);
      }
    }
    .user-page-unscoped.orange {
      #profileColor(@peach);
    }
    .user-page-unscoped.green {
      #profileColor(@green);
    }
    .user-page-unscoped.purple {
      #profileColor(@mauve);
    }
    .user-page-unscoped.red {
      #profileColor(@red);
    }
    .user-page-unscoped.blue {
      #profileColor(@blue);
    }
    .user-page-unscoped.pink {
      #profileColor(@pink);
    }
    .user-page-unscoped.gray {
      #profileColor(@overlay1);
    }
    .history-day {
      --color-foreground-grey-dark: #lib.rgbify(@crust)[];
    }
    .x-axis {
      --color-background-300: #lib.rgbify(@surface0)[];
    }
    /* Fixes gap between stats */
    @media (max-width: 1540px) {
      .stats-wrap {
        grid-row-gap: 25px;
      }
    }

    /* ----Tracking Status List Stuff---- */
    .list[data-v-5776f768] {
      color: @mantle !important;
    }
    .media-card .list-status[status="Repeating"] {
      background: @pink;
    }
    .banner .el-icon-arrow-down::before {
      color: @text !important;
    }
    .el-textarea__inner::placeholder {
      color: @overlay1;
    }
    .el-dropdown-menu,
    .el-dropdown-menu__item--divided::before,
    .el-select-dropdown {
      background-color: @surface0 !important;
    }
    .el-popper[x-placement^="bottom"] .popper__arrow::after,
    .el-popper[x-placement^="bottom"] .popper__arrow,
    .el-tooltip__popper[x-placement^="bottom"] .popper__arrow::after,
    .el-tooltip__popper[x-placement^="bottom"] .popper__arrow {
      border-bottom-color: @surface0;
    }
    .el-popper[x-placement^="top"] .popper__arrow::after,
    .el-popper[x-placement^="top"] .popper__arrow,
    .el-tooltip__popper[x-placement^="top"] .popper__arrow,
    .el-tooltip__popper[x-placement^="top"] .popper__arrow::after {
      border-top-color: @surface0;
    }
    .el-dropdown .feed-filter {
      color: @overlay0;
    }
    .el-dropdown-menu {
      border-color: @text !important;
    }
    .el-dropdown-menu__item--divided when not(@flavor = latte) {
      border-top-color: @surface1;
    }
    .el-select-dropdown__item.hover,
    .el-select-dropdown__item:hover {
      background-color: @base;
    }
    .el-select-dropdown__item.selected {
      color: @accent;
    }
    .el-tooltip__popper.is-dark {
      background: @surface0;
      color: @text;
    }
    .tooltip > div {
      --color-white: #lib.rgbify(@text)[];
    }
    .dropdown {
      --color-gray-100: #lib.rgbify(@base)[];
      --color-background-100: #lib.rgbify(@base)[];
      --color-background-200: #lib.rgbify(@mantle)[];
    }
    .medialist .notes {
      --color-overlay: #lib.rgbify(@surface0)[];
    }
    .el-input__inner::placeholder,
    .el-input__prefix {
      color: @overlay1;
    }
    .el-picker-panel {
      color: @text;
      background-color: @surface0;
      border-color: @surface1;
    }
    .el-date-table th {
      color: @overlay1;
      border-bottom-color: @surface1;
    }
    .el-date-picker__header-label {
      color: @subtext0;

      &:hover {
        color: @accent;
      }
    }
    .el-picker-panel__icon-btn {
      color: @overlay1;

      &:hover {
        color: @accent;
      }
    }
    .el-date-table td.available:hover {
      color: @accent;
    }
    .el-date-table td.current:not(.disabled) span {
      color: @crust;
      background-color: @accent;
    }
    .el-date-table td.next-month,
    .el-date-table td.prev-month {
      color: @surface2;
    }

    /* ---------SCORE TWEAKS-------------- */
    .score[data-v-5ca094da] {
      color: @base;
    }

    /* --------------FOOTER--------------- */
    .footer[data-v-0f519cab] {
      background: @text;
      color: @crust;

      h2 {
        color: rgb(var(--color-blue));
      }

      & when (@flavor = latte) {
        --color-blue: #lib.rgbify(lighten(@accent, 30%))[];
      }

      & when not(@flavor = latte) {
        background: @mantle;
        color: @subtext0;
      }
    }

    /* ------------------SEARCH PAGES------------------ */
    .search,
    .expand-description,
    .studio {
      --color-background-200: #lib.rgbify(@crust)[];
    }
    .search-wrap,
    .filters,
    .extra-filters-wrap,
    .select,
    .options {
      --color-background-100: #lib.rgbify(@base)[];
    }
    .filter.select-wrap .options {
      --color-background-100: #lib.rgbify(@mantle)[];
    }

    /* ------------------MISC EDITS-------------------- */
    .list-editor-wrap .header::after {
      background: fade(@mantle, 50%);
    }
    .filter.clear-all {
      --color-background-500: #lib.rgbify(@overlay1)[];
    }
    /* --Reset button in notifications----- */
    .reset-btn {
      color: @base !important;
    }
    /* Publish Button in Replies and Messages */
    .save {
      color: @base !important;
    }
    .el-radio {
      color: @overlay1;
    }
    /* Mod Stuff */
    .el-color-dropdown__link-btn {
      color: @accent;
    }
    .el-color-picker__color {
      border-color: @surface1;
    }
    .el-color-picker__panel {
      background-color: @surface0;
      border-color: @surface1;
    }
    .el-color-dropdown__btn {
      border-color: @surface2;
      color: @subtext1;

      &:hover {
        background-color: transparent;
        border-color: @accent;
        color: @accent;
      }
    }
    /* Accent Text */
    .input .hint {
      --color-white: #lib.rgbify(@text)[];
    }
    .el-checkbox__input.is-checked + .el-checkbox__label {
      color: @accent;
    }
    .button,
    .option.active,
    .new-badge,
    .submissions .user .stats > div,
    .actions .icon,
    .submissions .status,
    .adult-label,
    .header .btn,
    .media-tag-editor .btn,
    [data-v-843c77a2] .btn,
    [data-v-22855988] .btn,
    .btn[data-v-156b3973],
    .btn[data-v-dceb4a66],
    .favourite-btn,
    .dialog .btn,
    .tag,
    .filter,
    .media .header div,
    .list-editor-wrap .header .save-btn,
    .actions > div:not(.donator-badge),
    .banner-content .mod-badge-wrap,
    .external-link.no-color,
    .count.circle,
    .el-dropdown-menu__item,
    .delete,
    .list-editor-wrap .list-editor .body .delete-btn,
    .open-user-search,
    .save-btn {
      & when not(@flavor = latte) {
        --color-white: #lib.rgbify(@crust)[];
      }
    }
    .submit-btn,
    .random-btn {
      & when not(@flavor = latte) {
        color: @crust;
      }
    }
    .user .notification-dot,
    .create-btn {
      & when not(@flavor = latte) {
        color: @crust !important;
      }
    }
    .el-radio__inner::after {
      & when not(@flavor = latte) {
        background-color: @crust;
      }
    }
    .icon,
    .check {
      & when not(@flavor = latte) {
        --color-blue-300: #lib.rgbify(@crust)[];
      }
    }
    /* ------------IMAGE COVER------------ */
    .cover .image-text,
    .plus-progress {
      color: @text !important;
    }

    .entry .plus-progress,
    .medialist.table .entry .edit {
      color: @base !important;
    }
    /* --------SETTINGS-------- */
    .el-radio__inner when not(@flavor = latte) {
      background-color: @text;
    }
    .el-checkbox__input.is-checked .el-checkbox__inner,
    .el-radio__input.is-checked .el-radio__inner {
      background-color: @accent;
      border-color: @accent;
    }
    .el-checkbox__inner::after {
      border-color: @crust;
      border-width: 2px;
      left: 3px;
    }
    .el-radio__input.is-checked + .el-radio__label {
      color: @accent;
    }
    /* --------HIDE SCROLLBAR IN BIO----- */
    .section::-webkit-scrollbar {
      display: none;
    }
    .section {
      -ms-overflow-style: none;
      scrollbar-width: none;
    }
    /* --------REMOVE DROPDOWN SHADOWS----- */
    .dropdown.dropdown {
      box-shadow: none;
    }
    /* ---------ANILIST LIKER PLUGIN------ */
    #AnilistLikerPlugin .btntop {
      margin-bottom: -4px;
      margin-top: 5px;
    }
    #AnilistLikerPlugin .btnbottom {
      margin-top: -2px;
    }
    /* ---------AUTOMAIL TWEAKS--------- */
    .hohMediaScore {
      border-color: @surface0;
      padding: 4px;
      border-radius: 10px;
      margin-left: 0;
      background-color: @base;
      color: @accent;
    }
    .activity-feed .hohNoteSuffix:not(:empty),
    .activity-feed .hohRewatchSuffix:not(:empty),
    .activity-feed .hohScore:not(.hohSmiley),
    .hohFeedScore .hohScore {
      border-radius: 5px;
      padding: 2.5px 5px 1px !important;
      color: @subtext1 !important;
      background-color: @surface0;

      &:not(:first-of-type) {
        margin: 3px;
      }
    }
    .activity-feed .hohRewatchSuffix:not(:empty) {
      padding-left: 2px !important;
    }
    .hohExtraFilters button {
      margin-bottom: 20px;
    }
    .filter-group.compare-btn,
    .hohButton {
      color: @base;
    }
    .hohButton,
    .hohCheckbox input:checked + .el-checkbox__inner {
      background-color: @accent;
    }
    .hohCheckbox input:checked + .el-checkbox__inner {
      border-color: @accent;
    }
    .medialist.table .entry:hover,
    .medialist.table .entry .title a:hover,
    .compare-btn[data-v-170179fc]:hover {
      color: @base;
    }
    .hohCompare table {
      background-color: @crust;
    }
    .hohAnimeTable,
    .hohAnimeTable td,
    .hohUserRow td,
    .hohUserRow th,
    .hohHeaderRow td,
    .hohHeaderRow th {
      border-color: @surface0 !important;
    }
    .hohUserRow td {
      border-top-color: @surface0 !important;
    }
    .hohUserRow tr {
      border-right-color: @surface0 !important;
    }
    hr {
      color: @surface0;
    }
    .hohCheckbox
      input:checked
      + .el-checkbox__inner
      .like-wrap.thread
      .button.liked
      .fa-heart,
    .actions .favourite.liked .fa-heart,
    .like-wrap.thread .button.isFavourite .fa-heart,
    .actions .favourite.isFavourite .fa-heart,
    .actions .favourite {
      & when not(@flavor = latte) {
        --color-white: #lib.rgbify(@crust)[];
      }
    }
  }
}

@-moz-document domain("anichart.net") {
  body:not(.site-theme-dark) {
    #catppuccin(@lightFlavor);
  }
  .site-theme-dark {
    #catppuccin(@darkFlavor);
  }

  #catppuccin(@flavor) {
    #lib.palette();
    #lib.defaults();

    .login[data-v-0d5bfbcd],
    .login[data-v-0d5bfbcd]:hover {
      color: @base;
    }

    [fill="#3db4f2" i] {
      fill: @accent !important;
    }

    & when not(@flavor = latte) {
      .nav {
        background: @mantle;
      }

      .external-link {
        background: @surface1;

        [stroke="#fff" i] {
          stroke: @text !important;
        }
      }

      .external-link .icon,
      .dropdown-menu:hover {
        --color-white: #lib.rgbify(@crust)[];
      }

      [fill="#fff" i] {
        fill: @crust !important;
      }
    }

    & when (@flavor = latte) {
      --color-overlay: #lib.rgbify(@text)[];

      .external-link {
        background: @subtext0;
      }

      .nav {
        background: @text;
      }

      a.title {
        --color-white: #lib.rgbify(@base)[];
      }
    }
  }
}

/* ==UserStyle==
@name Bluesky Social Catppuccin
@namespace github.com/catppuccin/userstyles/styles/bsky
@homepageURL https://github.com/catppuccin/userstyles/tree/main/styles/bsky
@version 2025.10.29
@updateURL https://github.com/catppuccin/userstyles/raw/main/styles/bsky/catppuccin.user.less
@supportURL https://github.com/catppuccin/userstyles/issues?q=is%3Aopen+is%3Aissue+label%3Absky
@description Soothing pastel theme for Bluesky Social
@author Catppuccin
@license MIT

@preprocessor less
@var select lightFlavor "Light Flavor" ["latte:Latte*", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkFlavor "Dark Flavor" ["latte:Latte", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve*", "red:Red", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]
==/UserStyle== */

@import "https://userstyles.catppuccin.com/lib/lib.less";

@-moz-document domain("bsky.app") {
  html.colorMode--system {
    @media (prefers-color-scheme: light) {
      #catppuccin(@lightFlavor);
    }
    @media (prefers-color-scheme: dark) {
      #catppuccin(@darkFlavor);
    }
  }

  html.theme--light {
    #catppuccin(@lightFlavor);
  }
  html.theme--dim,
  html.theme--dark {
    #catppuccin(@darkFlavor);
  }

  #catppuccin(@flavor) {
    #lib.palette();
    #lib.defaults();

    // fix general site background - this applies to the <html> element
    background-color: @base;

    --text: @text;
    --background: @base;
    --backgroundLight: @base;

    /* generic text */
    [style*="color: rgb(255, 255, 255)"],
    [style*="color: rgb(241, 243, 245)"] //,
    /* [style*="color: rgb(11, 15, 20)"] this breas on dark */ {
      color: if(@flavor = latte, @base, @text) !important;
    }

    /* some background color of follow button + hover */
    [style*="background-color: rgb(241, 243, 245)"] {
      background-color: if(@flavor = latte, @crust, @text) !important;
    }

    [style*="color: rgb(11, 15, 20)"] {
      color: if(@flavor = latte, @text, @crust) !important;
    }

    /* secondary text [contrast_700] */
    [style*="rgb(165, 178, 197)"], // dark
    [style*="rgb(171, 184, 201)"], // dim
    [style*="rgb(84, 110, 138)"],
    [style*="rgb(66, 87, 108)"] {
      color: @subtext1 !important;
    }

    // white bottons on the theme/color mode selection in settings
    div[style*="background-color: rgb(197, 207, 217)"], // dark
    div[style*="background-color: rgb(201, 210, 219)"], // dim
    div[style*="background-color: rgb(46, 63, 81)"] // light
    {
      background-color: @text !important;
      > div {
        color: @crust !important;
      }
    }

    /* generic blue inline (e.g. link text, etc) [primary_500] */
    [style*="color: rgb(0, 106, 255)"], // dark
    [style*="color: rgb(15, 115, 255)"] // dim
    {
      color: @accent !important;
    }
    /* generic blue inline underline (links) [primary_500] */
    [style*="text-decoration-color: rgb(0, 106, 255)"], // dark
    [style*="text-decoration-color: rgb(15, 115, 255)"] // dim
    {
      text-decoration-color: @accent !important;
    }

    /* generic blue inline bg color (e.g. notification count) [primary_500] */
    [style*="background-color: rgb(0, 106, 255)"], // dark
    [style*="background-color: rgb(15, 115, 255)"] // dim
    {
      background-color: @accent !important;

      /* notification count contrast fix, also chat message contrast fix (> div) */
      &[style*="color: rgb(241, 243, 245)"],
      &[style*="color: rgb(255, 255, 255)"],
      > div {
        color: @crust !important;
      }

      /* icon color fix */
      path[fill="#ffffff"],
      path[fill="hsl(211, 20%, 95.3%)"] {
        fill: @crust !important;
      }
    }

    /* sending/pending/failed direct message */
    [style*="background-color: rgb(1, 64, 132)"] {
      background-color: fade(@accent, 30%) !important;
    }

    /* a lot of blue things (buttons etc) */
    .r-wzwllv,
    div[style*="background-color: rgb(0, 133, 255)"] {
      background-color: @accent !important;

      /* notification count contrast fix, also chat message contrast fix (> div) */
      &[style*="color: rgb(241, 243, 245)"],
      > div {
        color: @crust !important;
      }
    }

    /* subscribe to labeler button */
    button[data-testid="toggleSubscribeBtn"] {
      > div {
        background-color: @mauve !important;
        > div {
          color: @crust !important;
        }
      }
    }

    /* direct message "failed to send" text */
    span[style*="color: rgb(205, 10, 55)"] {
      color: @red !important;
    }

    /* red button text (delete my account) [negative_500] */
    [style*="color: rgb(233, 22, 70)"], // dark
    [style*="color: rgb(235, 36, 82)"] // dim
    {
      color: @red !important;
    }

    /* red button (delete, etc) */
    button[style*="background-color: rgb(244, 11, 66)"],
    // dark, light (same color)
    button[style*="background-color: rgb(246, 60, 103)"], // dark (hover)
    button[style*="background-color: rgb(245, 41, 88)"], // dim
    button[style*="background-color: rgb(247, 84, 122)"], // dim (hover)
    button[style*="background-color: rgb(205, 10, 55)"] // light (hover)
    {
      background-color: @red !important;
      &:hover {
        background-color: fade(@red, 70%) !important;
      }
      // text color
      > div {
        color: @crust !important;
      }
    }

    /* delete account button */
    [style^="background-color: rgb(79, 3, 20)"] {
      background-color: @red !important;
    }
    path[fill="#fba2b2"] {
      fill: @crust;
    }

    /* generic background color */
    [style*="background-color: rgb(255, 255, 255)"],
    [style*="background-color: rgb(0, 0, 0)"],
    [style*="background-color: rgb(21, 29, 40)"] // dim
    {
      background-color: @base !important;

      & when (@flavor = latte) {
        /* fix button colors in latte */
        [style*="background-color: rgb(0, 0, 0)"] {
          background-color: @text !important;
        }
      }
    }

    /* secondary background color + sidebar button hover [contrast_25] */
    [style*="background-color: rgb(17, 24, 34)"], // dark
    [style*="background-color: rgb(28, 39, 54)"] // dim
    {
      background-color: @mantle !important;
    }

    /* secondary background color (when active?) (e.g. search bar, hovering some buttons like cancel) */
    [style*="background-color: rgb(25, 34, 46)"], // dark
    [style*="background-color: rgb(34, 46, 63)"] // dim
    {
      background-color: @crust !important;
    }

    /* some sort of tertiary background color with a slight accent */
    [style*="background-color: rgb(0, 25, 51)"] {
      background-color: fade(@accent, 30%) !important;
    }

    /* loading circle */
    circle[style^="stroke: rgb(25, 118, 210)"] {
      stroke: @accent !important;
    }

    /* wide site: new post button (w text) */
    button[style*="background-color: rgb(0, 106, 255)"], // dark
    button[style*="background-color: rgb(66, 145, 255)"], // dark (hover)
    button[style*="background-color: rgb(15, 115, 255)"], // dim
    button[style*="background-color: rgb(77, 151, 255)"] // dim (hover)
    {
      background-color: @accent !important;
      &:hover {
        background-color: fade(@accent, 70%) !important;
      }

      > div {
        color: @crust !important;
      }
      svg > path {
        fill: @crust !important;
      }
    }

    /* borders */
    .css-175oi2r {
      border-color: @surface0 !important;
    }

    /* fix selected tab bottom border issue caused by selector above */
    .css-175oi2r[style*="border-bottom-color: rgb(0, 106, 255)"] {
      border-bottom-color: @accent !important;
    }

    /* fixes the bottom border on tabs (timeline) having a darker color */
    .r-oucylx {
      border-bottom-color: @base !important;
    }

    /* current selected tab (acc page) !! KEEP THIS BELOW ABOVE DEFINITION !! */
    [style*="border-color: rgb(255, 255, 255)"] {
      border-color: @accent !important;
    }

    /* settings highlight thing */
    [style="background-color: rgb(38, 39, 45)"] {
      background-color: @crust !important;
    }

    /* thin site: bluesky text */
    .r-yovm8p {
      color: @blue;
    }

    /* empty profile banner */
    .r-wuyfte {
      background-color: @blue;
    }

    /* "invalid handle" warning */
    [style*="color: rgb(236, 72, 104); border-color: rgb(209, 16, 67)"] {
      color: @red !important;
      border-color: @red !important;
    }

    /* end of feed text */
    .r-1521r3q {
      color: @overlay1;
    }

    /* mobile side drawer */
    [data-testid="drawer"] {
      background-color: @base;
    }

    /* feedback */
    .r-m7buhn {
      background-color: @blue;
    }

    /* save, report, post modal button */
    [data-testid="sendReportBtn"],
    [data-testid="composerPublishBtn"],
    [data-testid="editProfileSaveBtn"] {
      background: unset !important;

      /* "save changes" button in edit profile */
      > div[style*="background-image: linear-gradient(94.0544deg, rgb(90, 113, 250), rgb(0, 133, 255))"] {
        background: @accent !important;
      }

      /* fix contrast issues */
      > div,
      > div > div {
        color: @crust !important;
      }
    }

    [data-testid="saveBtn"] {
      > div[style*="background-image: linear-gradient"] {
        background: unset !important;
        background-color: @accent !important;
        > div {
          color: @crust !important;
        }
      }
    }

    // toast notifications (e.g. "copied to clipboard")
    .r-17c3jg3:has(> div[dir="auto"]) {
      background-color: @crust !important;

      .r-jwli3a {
        color: @text !important;
      }
      > svg > path {
        fill: @text !important;
      }
    }

    /* draft "Discard" button */
    [style^="background-color: rgb(209, 16, 67)"] {
      background-color: @red !important;
    }

    /* Discard draft modal border, bunch of other borders around the app [contrast_100] */
    [style*="border-color: rgb(35, 46, 62)"], // dark
    [style*="border-color: rgb(44, 58, 78)"] // dim
    {
      border-color: @surface2 !important;
    }

    /* the round gradient post button in the bottom right when the page is thinner than usual */
    [data-testid="composeFAB"] > div {
      background: unset !important;
      background-color: @accent !important;

      // fix contrast (inner symbol)
      .r-jwli3a {
        color: @crust !important;
      }
    }

    /* blue text only button (e.g. cancel post button) */
    div[style*="color: rgb(66, 145, 255)"], // dark
    div[style*="color: rgb(77, 151, 255)"], // dim
    div[style*="color: rgb(0, 89, 214)"] // latte !! this overlaps with dark. might break?
    {
      color: @accent !important;
    }

    /* text color when writing a post */
    .ProseMirror,
    .ProseMirror-dark {
      color: @text;
    }

    /* some button background color (when hovering) [primary_100] */
    [style*="background-color: rgb(0, 40, 97)"], // dark
    [style*="background-color: rgb(18, 52, 100)"], // dim
    [style*="background-color: rgb(204, 225, 255)"], // latte
    /* add alt text modal */
    [style*="background-color: rgb(14, 73, 137)"] {
      background-color: fade(@accent, 30%) !important;
    }

    /* several white buttons */
    button[style*="background-color: rgb(255, 255, 255)"] when not(
      @flavor = latte
    ) {
      background-color: @text !important;
    }

    /* the white buttons text */
    [style*="color: rgb(0, 0, 0)"] {
      color: if(@flavor = latte, @text, @crust) !important;
    }

    /* replace dark fill icons with crust ones */
    path[fill="#000000"] {
      fill: if(@flavor = latte, @text, @crust);
    }

    /* fixes a contrast issue when the suggested follows button is enabled (very ugly selector) */
    button[data-testid="suggestedFollowsBtn"][style*="background-color: rgb(255, 255, 255)"]
      > svg
      > path {
      fill: @base !important;
    }

    /* text inside most buttons */
    button > div > div[style~="color: rgb(0, 0, 0)"] {
      color: @base !important;
    }

    /* search bar text */
    [role="search"],
    [data-testid="confirmCodeInput"] {
      --placeholderTextColor: @subtext1 !important;
    }

    /* search bar icon (this doesn't actually apply because path fill. keeping anyways) [contrast_500] */
    svg[style^="color: rgb(102, 123, 153"], // dark
    svg[style^="color: rgb(111, 131, 159)"] // dim
    {
      color: @subtext1 !important;
    }

    /* small icons */
    path[fill="#FFFFFF"] {
      fill: @text;

      & when (@flavor = latte) {
        fill: @base;
      }
    }

    /* more gray small icons (e.g. search bar icon, icons below posts [contrast_500] */
    path[fill="#667B99"], // dark
    path[fill="#6F839F"], // dim
    [style*="color: rgb(111, 131, 159)"], // dark
    [style*="color: rgb(111, 131, 159)"] // dim
    {
      color: @subtext1 !important;
      fill: @subtext1 !important;
    }
    /* settings button at the top of the timeline, hashtag button/icon at top of timeline [contrast_600] */
    path[fill="#8798B0"], // dark
    path[fill="#8D9DB4"] // dim
    {
      fill: @subtext1;
    }

    /* x invite codes available icon */
    div[style*="background-color: rgb(191, 225, 255)"],
    div[style*="background-color: rgb(1, 37, 97)"] {
      background-color: fade(@accent, 30%) !important;
      > svg > path[fill="#52acfe"] {
        fill: @accent;
      }
    }

    /* heart/like */
    .r-84gixx,
    .r-1i89l9k,
    div[style*="color: rgb(236, 72, 153)"],
    button[data-testid="toggleLikeBtn"]
      > svg:has(path[fill^="hsl(346,"]) // feed like button
    {
      color: @red !important;
      > path {
        fill: @red !important;
      }
    }

    /* repost */
    .r-5ld2xk,
    [style*="color: rgb(92, 239, 170)"] {
      color: @green !important;
      > path {
        fill: @green !important;
      }
    }

    /* saved icon on post + trending icon */
    path[fill="#006AFF"], // dark
    path[fill="#0F73FF"] // dim
    {
      fill: @accent;
    }

    /* tiny people icon on post view ("Everybody can reply") */
    path[fill="hsl(211, 24%, 43.6%)"],
    /* icon above "you have no lists" */
    path[fill="hsl(211, 24%, 34.2%)"] {
      fill: @subtext0;
    }

    /* onboarding: continue/next button */
    [data-testid="continueBtn"] {
      background-color: @accent !important;
    }
    [data-testid="continueBtn"] > div > div {
      color: @base !important;
    }
    [data-testid="continueBtn"] > div > svg > path {
      fill: @base !important;
    }

    /* pretty self explanatory, user mentions in the post create modal */
    .mention {
      color: @accent;
    }

    /* mentions in the notification tab (highlighted notifications) */
    div[style*="background-color: rgb(0, 17, 36)"], // dark
    div[style*="background-color: rgb(21, 41, 63)"], // dim
    div[style*="background-color: rgb(240, 247, 255)"], // light
    a[style*="background-color: rgb(0, 17, 36)"], // dark
    a[style*="background-color: rgb(21, 41, 63)"], // dim
    a[style*="background-color: rgb(240, 247, 255)"] // light
    {
      background-color: fade(@accent, 30%) !important;
      .css-175oi2r:not([role="checkbox"] *) {
        background-color: transparent !important;
      }
    }

    /* generic blue background color inline (used for buttons in notifications tab) */
    button[style*="background-color: rgb(0, 133, 255)"] {
      background-color: @accent !important;
    }
    /* contrast fix for above */
    button[style*="background-color: rgb(0, 133, 255)"] > div > div {
      color: @base !important;
    }

    /* miscellaneous lines around the app [contrast_200] */
    [style*="border-color: rgb(49, 63, 84)"], // dark
    [style*="border-color: rgb(57, 73, 96)"] // dim
    {
      border-color: @surface1 !important;
    }

    /* other button in notifications tab, inner text */
    div[style*="color: rgb(82, 172, 254)"] {
      color: @accent !important;
    }

    /* "No notifications yet!" text in the notifications tab */
    div[style*="color: rgb(141, 142, 150)"] {
      color: @subtext0 !important;
    }

    /* login: wrong password highlight */
    .r-w7hjet {
      background-color: @red;
    }

    /* please verify your email popup (green checkmark) */
    circle[fill="#20BC07"] {
      fill: @green;
    }

    /* toggle circle */
    [style*="background-color: rgb(185, 185, 193)"] {
      background-color: @overlay2 !important;
    }

    /* skeletons */
    .r-kdyh1x,
    .r-cpet4d {
      &[style*="background-color: rgb(20, 27, 35);"] {
        background-color: @surface0 !important;
      }
    }

    /* thread lines */
    div[style*="background-color: rgb(35, 46, 62)"].r-m5arl1 {
      background: @surface1 !important;
    }

    /* character limit progress circle [primary_500] */
    path[stroke="#006AFF"], // dark
    path[stroke="#0F73FF"], // dim
    path[stroke="#006AFF"] // light
    {
      stroke: @accent !important;
    }

    /* bg circle [contrast_100] */
    path[stroke="#232E3E"], // dark
    path[stroke="#2C3A4E"], // dim
    path[stroke="#DCE2EA"] // light
    {
      stroke: @surface1 !important;
    }

    /* load new posts button (border: [contrast_100]; bg: [primary_50]) */
    button[style*="border-color: rgb(35, 46, 62); background-color: rgb(0, 30, 71)"],
    // dark
    button[style*="border-color: rgb(44, 58, 78); background-color: rgb(18, 41, 73)"],
    // dim
    button[style*="border-color: rgb(220, 226, 234); background-color: rgb(229, 240, 255)"] // light
    {
      background-color: @surface0 !important;
      border-color: @overlay0 !important;
    }

    /* any button that's disabled (we should start using broader overrides) */
    button[disabled] > svg > path {
      fill: @overlay0;
    }

    /* sitewide text inputs */
    /* the divs are styled separately from the input areas */
    div[style*="border-color: rgb(46, 64, 82)"] {
      border-color: @surface1 !important;
    }
    /* search bar [primary_500] */
    div[style*="border-color: rgb(0, 106, 255)"], // dark
    div[style*="border-color: rgb(15, 115, 255)"] // dim
    {
      border-color: @accent !important;
    }

    /* image preview borders across app */
    [style*="border-color: rgb(74, 97, 121)"] {
      border-color: @surface2 !important;
    }

    /* search tab trending buttons */
    [style*="background-color: rgb(142, 9, 40)"] {
      background-color: @red !important;

      div {
        color: @crust !important;
      }

      svg > path {
        fill: @crust;
      }
    }
    /* TODO: new which is green but I can't get the right class cause nothing on bsky is a new trending topic atm */

    /* round icon backgrounds (e.g. feeds) */
    div[style*="background-color: rgb(21, 52, 85)"] {
      background-color: @surface0 !important;
    }

    /* pill toggle button styles (e.g. account activity notifications) */
    /* active, hover */
    div[style*="border-color: rgb(76, 162, 254);"] {
      border-color: @accent !important;
    }
    div[style*="background-color: rgb(18, 52, 100)"] {
      background-color: fade(@accent, 40%) !important;
    }
    /* inactive, circle in pill */
    div[style*="background-color: rgb(91, 119, 149)"] {
      background-color: @surface2 !important;
    }
    /* inactive, hover */
    div[style*="border-color: rgb(120, 142, 165)"] {
      border-color: @overlay1 !important;
    }
  }
}

/* ==UserStyle==
@name docs.rs Catppuccin
@namespace github.com/catppuccin/userstyles/styles/docs.rs
@homepageURL https://github.com/catppuccin/userstyles/tree/main/styles/docs.rs
@version 2025.09.06
@updateURL https://github.com/catppuccin/userstyles/raw/main/styles/docs.rs/catppuccin.user.less
@supportURL https://github.com/catppuccin/userstyles/issues?q=is%3Aopen+is%3Aissue+label%3Adocs.rs
@description Soothing pastel theme for docs.rs
@author Catppuccin
@license MIT

@preprocessor less
@var select lightFlavor "Light Flavor" ["latte:Latte*", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkFlavor "Dark Flavor" ["latte:Latte", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve*", "red:Red", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]
==/UserStyle== */

@import "https://userstyles.catppuccin.com/lib/lib.less";

@-moz-document domain("docs.rs"), domain("doc.rust-lang.org") {
  :root:not([data-docs-rs-theme]) {
    @media (prefers-color-scheme: light) {
      #catppuccin(@lightFlavor);
    }
    @media (prefers-color-scheme: dark) {
