        '<svg width="17" height="17" shape-rendering="crispEdges" stroke="@{text}" fill="none" xmlns="http://www.w3.org/2000/svg"><path d="M5 2.5H2.5v12H5m7-12h2.5v12H12m-7-6h7"/></svg>'
      );

      > summary::before {
        background: url("data:image/svg+xml,@{plus}") no-repeat top left;
        filter: none;
      }

      &[open] > summary::before {
        background: url("data:image/svg+xml,@{minus}") no-repeat top left;
      }
    }

    .src #sidebar-button > a::before {
      @svg: escape(
        '<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 22 22" fill="none" stroke="@{text}"><path d="M16 9V5h-6V4L8 2H4L2 4v16h13l6-11H6L2 19" stroke-width="1.25"/><path d="M15 7H4v3" stroke-width=".75"/><path d="M3.75 10v1.25" stroke-width=".375"/></svg>'
      );
      content: url("data:image/svg+xml,@{svg}");
    }

    .pure-menu-link {
      color: @subtext0;

      &:hover {
        color: @text;
      }
    }
    .pure-table {
      &,
      td,
      th {
        border-color: @surface0;
      }
    }
  }
}

/* ==UserStyle==
@name GitHub Catppuccin
@namespace github.com/catppuccin/userstyles/styles/github
@homepageURL https://github.com/catppuccin/userstyles/tree/main/styles/github
@version 2025.12.05
@updateURL https://github.com/catppuccin/userstyles/raw/main/styles/github/catppuccin.user.less
@supportURL https://github.com/catppuccin/userstyles/issues?q=is%3Aopen+is%3Aissue+label%3Agithub
@description Soothing pastel theme for GitHub
@author Catppuccin
@license MIT

@preprocessor less
@var select lightFlavor "Light Flavor" ["latte:Latte*", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkFlavor "Dark Flavor" ["latte:Latte", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve*", "red:Red", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]
==/UserStyle== */

@import "https://userstyles.catppuccin.com/lib/lib.less";

@-moz-document /* Match every page on GitHub except github.com/home, github.com/features/*, and github.com/organizations/plan. */
regexp(
    "https:\/\/github\.com(?!(\/home$|\/features($|\/.*)|\/organizations\/plan)).*$"
  ),
  domain("gist.github.com"),
  domain("docs.github.com"),
  domain("viewscreen.githubusercontent.com") {
  [data-color-mode][data-color-mode="auto"] {
    @media (prefers-color-scheme: light) {
      &[data-light-theme="light"] {
        #catppuccin(@lightFlavor);
      }
      &[data-light-theme="dark"] {
        #catppuccin(@darkFlavor);
      }
    }

    @media (prefers-color-scheme: dark) {
      &[data-dark-theme="light"] {
        #catppuccin(@lightFlavor);
      }
      &[data-dark-theme="dark"] {
        #catppuccin(@darkFlavor);
        header[data-color-mode] {
          #catppuccin(@darkFlavor);
        }
      }
    }
  }
  [data-color-mode="light"][data-light-theme="dark"],
  [data-color-mode="dark"][data-dark-theme="dark"] {
    #catppuccin(@darkFlavor);
  }
  [data-color-mode="light"][data-light-theme="light"],
  [data-color-mode="dark"][data-dark-theme="light"] {
    #catppuccin(@lightFlavor);
